	@Transactional
	private createLoacalization(String lcCode){
		//copy localization for other languages
		Localization.withTransaction {status->
			LocaleLang.findAll().each {lang->
				Localization klon = new Localization(code:lcCode,locale: lang.id,text: lcCode )
				klon.save(flush:true,failOnError :false)
			}
		}
	}
	@Transactional
	private SmartSourceField createField(GrailsDomainClassProperty col,List<SmartSource> dsList){
		SmartSourceField dsField = new SmartSourceField()
		dsField.name=col.getName()
		switch (col.getType() ) {
			case String:
				dsField.type ="text"
				break
			case int:
			case Integer:
			case Long:
				dsField.type ="integer"
				break
			case Double:
			case double:
			case Float:
			case float:
			case BigDecimal:
				dsField.type = "localeFloat"
				break
			case Timestamp:
				dsField.type = "datetime"
				break
			case Date:
				dsField.type = "date"
				break
			case boolean:
			case Boolean:
				dsField.type = "boolean"
				break
			case byte[]:
			case SmartFile:
				dsField.type = "binary"
				dsField.editorType = "SmartFileItem"
			//				dsField.readOnlyEditorType = "FileItem"
				dsField.detail=true
				dsField.canSave=false
				break
			default:
				break
		}
		if(dsField.type==null){
			dsField.type="any"
			//asoc
			log.debug col.getTypePropertyName()
			if(col.manyToOne&&(col.owningSide||!col.otherSide?.owningSide)||col.oneToOne){
				dsField.optionDataSource=col.getReferencedDomainClass().getName()
				dsField.foreignKey = col.getReferencedDomainClass().getName().concat(".").concat(col.getReferencedDomainClass().getIdentifier().getName())
				dsField.valueField = col.getReferencedDomainClass().getIdentifier().getName()
				dsField.editorType = "SmartCombo"
				dsField.valueXPath = col.getName().concat("/").concat(col.getReferencedDomainClass().getIdentifier().getName())
				dsField.type="integer"


			}else{
				if(col.manyToOne&&col.otherSide?.owningSide){
					dsField.hidden=true
					dsField.type="integer"
					dsField.valueXPath = col.getName().concat("/").concat(col.getReferencedDomainClass().getIdentifier().getName())
				}
			}
			if(col.oneToMany){
				//				if(col.owningSide){
				//					//agregacia
				//					dsField.multiple=true
				//					dsField.multipleStorage="json"
				//					dsField.foreignKey = col.getReferencedDomainClass().getName().concat(".").concat(col.getReferencedDomainClass().getIdentifier().getName())
				//					dsField.type=col.getReferencedDomainClass().getName()
				//					dsField.hidden=true
				//					createDS(col.getReferencedDomainClass(),dsList)
				//
				//				}else{
				dsField.type=col.owningSide?"agrmany":"bref"
				dsField.optionDataSource=col.getReferencedDomainClass().getName()
				dsField.foreignKey=col.otherSide?.getName()
				dsField.hidden=true
				dsField.canSave=false

				//				}
			}
			if(col.getType().isEnum()){
				def tt = col.getType()
				log.debug "enum:"+(col.getType().values() as JSON)
				dsField.type="enum"
				def values=col.getType().values()*.name()
				//				String vals=""
				//				values.each {
				//					vals=vals.concat("'").concat(it).concat("',")
				//				}
				//				vals=vals?.substring(0,vals.length()-1)
				dsField.valueMap=values.join(",")
				dsField.valueXPath=col.getName()+"/name"
			}
		}else{
			def constrProps = col.domainClass.constrainedProperties
			if(constrProps.containsKey(dsField.name)){
				ConstrainedProperty constr = (ConstrainedProperty) constrProps.get(dsField.name);
				dsField.length=constr.maxSize
				dsField.required=!constr.isNullable()
				dsField.hidden=!constr.isDisplay()
				dsField.decimalPrecision=constr.scale
				dsField.decimalPad=constr.scale
			}
		}
		if(dsField.type=="boolean"&&!dsField.required) dsField.formItemProps = "{\"allowEmptyValue\":true}"
		//auto fields
		String autoKey = autoFields.find{
			it.value.name==dsField.name
			}?.key
		if(autoKey){
			dsField.autoGenerated=true
			dsField.canSave=false
			dsField.canEdit=false
			dsField.detail=true
			dsField.type=autoFields.get(autoKey)?.type?.isCase(new Date())  ?"datetime":dsField.type
			dsField.title="isc.i18nMessages[\"default."+autoKey+".label\"]"
			dsField.required=false
			//			createLoacalization("default."+dsField.getName()+".label")
		}else{
			dsField.canFilter=true
			dsField.canSave=true
			dsField.canFocus=true
			dsField.canEdit=null
			dsField.title="isc.i18nMessages[\""+col.domainClass.getName()[0].toLowerCase()+col.domainClass.getName().substring(1)+"."+dsField.getName()+".label\"]"
			createLoacalization(col.domainClass.getName()[0].toLowerCase()+col.domainClass.getName().substring(1)+"."+dsField.getName()+".label")
		}
		log.debug "Persistent property:"+(dsField as JSON)
		return dsField
	}
	
