	@Transactional
	private mergeDS(GrailsDomainClass dom, SmartSource ds){
      if(dom.getFullName().contains("com.sfs.smartsfs.sec")) return
      List<SmartSource> dsList = new ArrayList<SmartSource>()
      def smartFileFields=[]
      Boolean newField = false
      if(ds){
         ds.refresh()
         int fieldPos = ds.getFields()?.max{it.fieldPosition}?.fieldPosition?:0
         dom.getPersistentProperties().each {col->
            def fld = ds.getFields().find{it.name==col.getName()}
            if(!fld){
               newField = true
               SmartSourceField dsField = createField(col,dsList)
               dsField.setFieldPosition(++fieldPos)
               if(dsField.editorType == "SmartFileItem"){
                  //budeme pridavat _filename field
                  smartFileFields<<dsField
               }
               log.debug "Add FIELD ${dsField.name} position ${dsField.fieldPosition} to ${ds.ID}"
               ds.addToFields(dsField)
               log.debug "check:${ds.getFields().last().name}"
            }
         }
         if(smartFileFields.size()>0){
            //pre smartFile fields dopln _filename hidden field, aby fungoval widget
            smartFileFields.each {SmartSourceField fld->
               dsField = new SmartSourceField(
                  name:"${fld.name}_filename",fieldPosition : fieldPos++,type:"text",
                  valueXPath:"${fld.name}/filename",canSave:false,canEdit:false,hidden:true)
               ds.addToFields(dsField)
            }
         }
         if(newField){
            SmartSource.withTransaction {status->
               try {
                  ds.getFields()*.validate()
                  ds.validate()
                  if(ds.hasErrors()){
                     log.debug "HAS ERRORS:"+ds.errors
                  }else {
                     ds.save(flush:true)
                     log.debug "After SAVE ${ds.getFields().size()}"
                  }
   
               }catch (Exception e) {
                     log.debug "EXCEPTION!!!!:"+e.getMessage()
                     status.setRollbackOnly()
                  }
            }
         }
      }
   }
	
	@Transactional
	private createDS(GrailsDomainClass dom,List<SmartSource> dsList){
		def smartFileFields=[]
		SmartSource ds = new SmartSource()
		ds.setID(dom.getName())
		ds.title = "isc.i18nMessages[\""+dom.getName()[0].toLowerCase()+dom.getName().substring(1)+".label\"]"
		ds.titleField = dom.getIdentifier().getName()
		createLoacalization(dom.getName()[0].toLowerCase()+dom.getName().substring(1)+".label")
		ds.pluralTitle= "isc.i18nMessages[\""+dom.getName()[0].toLowerCase()+dom.getName().substring(1)+".plural.label\"]"
		createLoacalization(dom.getName()[0].toLowerCase()+dom.getName().substring(1)+".plural.label")
		ds.dataURL = "/"+dom.getName()+"/custom.json"
		ds.addDataURL = "/"+dom.getName()+"/save.json"
		ds.fetchDataURL = "/"+dom.getName()+"/index.json"
		ds.removeDataURL = "/"+dom.getName()+"/delete.json"
		ds.updateDataURL = "/"+dom.getName()+"/update.json"
		ds.dataFormat = "json"
		ds.disableQueuing=true
		ds.jsonRecordXPath = "/response/data"
		ds.canMultiSort=true
		
		ds.setContextRoot(this.ctxRoot)

		ds.fields = new TreeSet()

		int fieldPos = 1
		//identifikator
		SmartSourceField dsField = createField(dom.getIdentifier(),dsList)
		dsField.fieldPosition = fieldPos++
		dsField.primaryKey=true
		if(dsField.type=="integer"){
			dsField.autoGenerated=true
			dsField.canEdit=false
			dsField.canSave=false
		}
		ds.addToFields(dsField)

		dom.getPersistentProperties().each {col->
			dsField = createField(col,dsList)
			dsField.fieldPosition = fieldPos++
			if(dsField.editorType == "SmartFileItem"){
				//budeme pridavat _filename field
				smartFileFields<<dsField
			}
			ds.addToFields(dsField)
			
		}
		if(smartFileFields.size()>0){
			//pre smartFile fields dopln _filename hidden field, aby fungoval widget
			smartFileFields.each {SmartSourceField fld->
				dsField = new SmartSourceField(
					name:"${fld.name}_filename",fieldPosition : fieldPos++,type:"text",
					valueXPath:"${fld.name}/filename",canSave:false,canEdit:false,hidden:true)
				ds.addToFields(dsField)
			}
		}
		if(dom.getVersion()) {
			dsField = createField(dom.getVersion(),dsList)
			dsField.fieldPosition = fieldPos++
			dsField.autoGenerated=true
			dsField.hidden=true

			ds.addToFields(dsField)
		}
		log.debug "save new Datasource:"+(ds as JSON)
		dsList << ds
	}

	@Transactional
	def createDataSources() {

		GrailsDomainClass[] domList = grailsApplication.getArtefacts("Domain")
		def contrList = grailsApplication.getArtefacts("Controller")

		domList.each {dom->
			if(dom.getName()=="SmartFile") {return}
			
			SmartSource ds = SmartSource.findWhere(ID:dom.getName())
			def contrNm = dom.getName()+"Controller"
			def contr = contrList.find {
				it.getShortName()==contrNm
			}
			if(!ds&&contr){
				log.debug "create new DataSource ID:"+dom.getName()

				SmartSource.withTransaction {status->
					try {
						List<SmartSource> dsList = new ArrayList<SmartSource>()
						createDS(dom,dsList)
						dsList.each {d->
							d.getFields()*.validate()
							d.validate()
							if(d.hasErrors()){
								log.debug "HAS ERRORS:"+d.errors
							}else d.save(flush:true)
						}
						//						dsList*.save flush:true
					} catch (Exception e) {
						log.debug "EXCEPTION!!!!:"+e.getMessage()
						status.setRollbackOnly()
					}
				}

			}else if(ds){
            mergeDS(dom, ds)
         }
		}
		//		//asi zrobil OTP treba zmazat
		//		UserOtp admOtp=UserOtp.findByUsername("admin")
		//		if(admOtp){
		//			admOtp.otpKey=null
		//			admOtp.save(flush:true)
		//		}

	}

